import React, { useState, useEffect } from 'react';
import Card from './Card';
import Pokeinfo from './Pokeinfo';
import axios from 'axios';

const Main = () => {
  const [pokeData, setPokeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [url, setUrl] = useState('https://pokeapi.co/api/v2/pokemon/');
  const [nextUrl, setNextUrl] = useState();
  const [pokeDex, setPokeDex] = useState();

  const fetchData = async (fetchUrl) => {
    try {
      setLoading(true);
      const response = await axios.get(fetchUrl);
      const { results, next } = response.data;
      const pokemonDetails = await Promise.all(
        results.map(async (result) => {
          const res = await axios.get(result.url);
          return res.data;
        })
      );
      setPokeData((prevData) => [...prevData, ...pokemonDetails]);
      setNextUrl(next);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching data:', error);
      setLoading(false);
    }
  };

  const handleScroll = () => {
    const scrollTop = document.documentElement.scrollTop || document.body.scrollTop;
    const windowHeight = window.innerHeight || document.documentElement.clientHeight || document.body.clientHeight;
    const fullHeight = document.documentElement.scrollHeight || document.body.scrollHeight;

    if (scrollTop + windowHeight >= fullHeight && !loading && nextUrl) {
      fetchData(nextUrl);
    }
  };

  useEffect(() => {
    fetchData(url);

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <div className="container">
        <div className="left-content">
          <Card pokemon={pokeData} loading={loading} infoPokemon={(poke) => setPokeDex(poke)} />

          <div className="btn-group">
            {/* No need for the previous button */}
            {nextUrl && (
              <button
                onClick={() => {
                  setPokeData([]);
                  setUrl(nextUrl);
                }}
              >
                Next
              </button>
            )}
          </div>
        </div>
        <div className="right-content">
          <Pokeinfo data={pokeDex} />
        </div>
      </div>
    </>
  );
};

export default Main;
