import React, { useState, useEffect } from 'react';
import Card from './Card';
import Pokeinfo from './Pokeinfo';
import axios from 'axios';

const Main = () => {
  const [pokeData, setPokeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [url, setUrl] = useState('https://pokeapi.co/api/v2/pokemon/');
  const [nextUrl, setNextUrl] = useState(null);
  const [prevUrl, setPrevUrl] = useState(null);
  const [pokeDex, setPokeDex] = useState();

  const fetchPokeData = async () => {
    setLoading(true);
    const res = await axios.get(url);
    setNextUrl(res.data.next);
    setPrevUrl(res.data.previous);
    getPokemon(res.data.results);
    setLoading(false);
  };

  const getPokemon = async (results) => {
    const uniqueUrls = results
      .map((item) => item.url)
      .filter((url) => !pokeData.find((poke) => poke.url === url));
    const pokemonPromises = uniqueUrls.map((url) => axios.get(url));
    const pokemonData = await Promise.all(pokemonPromises);
    const pokemonDetails = pokemonData.map((res) => res.data);
    setPokeData((prevData) => [...prevData, ...pokemonDetails]);
  };

  const handleScroll = () => {
    if (
      window.innerHeight + document.documentElement.scrollTop ===
      document.documentElement.offsetHeight
    ) {
      // Reached the bottom of the page, fetch more data
      if (nextUrl) {
        fetchPokeData();
      }
    }
  };

  useEffect(() => {
    fetchPokeData();

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [nextUrl]);

  return (
    <>
      <div className="container">
        <div className="left-content">
          <Card
            pokemon={pokeData}
            loading={loading}
            infoPokemon={(poke) => setPokeDex(poke)}
          />
        </div>
        <div className="right-content">
          <Pokeinfo data={pokeDex} />
        </div>
      </div>
    </>
  );
};

export default Main;
