import React, { useState } from 'react';
import './SearchPage.css';

const SearchPage = () => {
  const [pokemonName, setPokemonName] = useState('');
  const [pokemonData, setPokemonData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleInputChange = (e) => {
    setPokemonName(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemonName.toLowerCase()}/`);
      if (response.ok) {
        const data = await response.json();
        // Extract the desired data from the API response
        const {
          stats: [
            { base_stat: hp },
            { base_stat: attack },
            { base_stat: defense },
            { base_stat: specialAttack },
            { base_stat: specialDefense },
            { base_stat: speed },
          ],
        } = data;

        // Create a new object with the extracted data
        const modifiedData = {
          hp,
          attack,
          defense,
          'special-attack': specialAttack,
          'special-defense': specialDefense,
          speed,
        };

        // Set the modified data in state
        setPokemonData(modifiedData);
      } else {
        console.log('Error:', response.statusText);
      }
    } catch (error) {
      console.log('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="search-page">
      <h1>Pokémon Search</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" value={pokemonName} onChange={handleInputChange} placeholder="Enter Pokémon name" />
        <button type="submit">Search</button>
      </form>

      {isLoading && <div className="loading">Loading...</div>}

      {pokemonData && (
        <div className="pokemon-details">
          <h2>{pokemonName}</h2>
          <p>HP: {pokemonData.hp}</p>
          <p>Attack: {pokemonData.attack}</p>
          <p>Defense: {pokemonData.defense}</p>
          <p>Special Attack: {pokemonData['special-attack']}</p>
          <p>Special Defense: {pokemonData['special-defense']}</p>
          <p>Speed: {pokemonData.speed}</p>
        </div>
      )}
    </div>
  );
};

export default SearchPage;
