import React, { useState, useEffect, useRef } from 'react';
import Card from './Card';
import Pokeinfo from './Pokeinfo';
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroll-component';

const Main = () => {
  const [pokeData, setPokeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [url, setUrl] = useState('https://pokeapi.co/api/v2/pokemon/');
  const [nextUrl, setNextUrl] = useState('');
  const [prevUrl, setPrevUrl] = useState('');
  const [pokeDex, setPokeDex] = useState('');
  const scrollContainerRef = useRef(null);

  const pokeFun = async () => {
    setLoading(true);
    const res = await axios.get(url);
    setNextUrl(res.data.next);
    setPrevUrl(res.data.previous);
    getPokemon(res.data.results);
    setLoading(false);
  };

  const getPokemon = async (res) => {
    for (let i = 0; i < res.length; i++) {
      const result = await axios.get(res[i].url);
      setPokeData((state) => {
        state = [...state, result.data];
        state.sort((a, b) => (a.id > b.id ? 1 : -1));
        return state;
      });
    }
  };

  useEffect(() => {
    pokeFun();
    const storedPosition = sessionStorage.getItem('scrollPosition');
    if (storedPosition && scrollContainerRef.current) {
      scrollContainerRef.current.scrollTo(0, parseInt(storedPosition));
    }
    return () => {
      if (scrollContainerRef.current) {
        sessionStorage.setItem(
          'scrollPosition',
          scrollContainerRef.current.scrollY.toString()
        );
      }
    };
  }, [url]);

  const loadMoreData = () => {
    if (nextUrl) {
      setUrl(nextUrl);
    }
  };

  return (
    <>
      <div className="container">
        <div className="left-content" ref={scrollContainerRef}>
          <Card
            pokemon={pokeData}
            loading={loading}
            infoPokemon={(poke) => setPokeDex(poke)}
          />

          <div className="btn-group">
            {prevUrl && (
              <button
                onClick={() => {
                  setUrl(prevUrl);
                }}
              >
                Previous
              </button>
            )}

            {nextUrl && (
              <InfiniteScroll
                dataLength={pokeData.length}
                next={loadMoreData}
                hasMore={true}
                loader={<button>Loading...</button>}
                scrollableTarget={scrollContainerRef.current}
              >
                <button
                  onClick={() => {
                    setUrl(nextUrl);
                  }}
                >
                  Next
                </button>
              </InfiniteScroll>
            )}
          </div>
        </div>
        <div className="right-content">
          <Pokeinfo data={pokeDex} />
        </div>
      </div>
    </>
  );
};

export default Main;
